mapper.insert:
insert into comment (comment_id, observer_account, article_id,
comment_time, reviewer_account, comment_content
)
values (#{commentId,jdbcType=BIGINT}, #{observerAccount,jdbcType=VARCHAR},
#{articleId,jdbcType=BIGINT},
#{commentTime,jdbcType=TIMESTAMP}, #{reviewerAccount,jdbcType=VARCHAR},
#{commentContent,jdbcType=LONGVARCHAR}
)

mapper.update:
update comment
set observer_account = #{observerAccount,jdbcType=VARCHAR},
article_id = #{articleId,jdbcType=BIGINT},
comment_time = #{commentTime,jdbcType=TIMESTAMP},
reviewer_account = #{reviewerAccount,jdbcType=VARCHAR}
where comment_id = #{commentId,jdbcType=BIGINT}

mapper.select:
select

,

from comment
where article_id = #{articleId,jdbcType=BIGINT}
and observer_account !=#{observerAccount,jdbcType=VARCHAR}
order by comment_time Desc
